pub enum ErrorKind<'v> {
    InvalidLength {
        min: Option<u64>,
        max: Option<u64>,
    },
    InvalidChoice {
        choices: Cow,
    },
    OutOfRange {
        start: Option<isize>,
        end: Option<isize>,
    },
    Validation(Cow),
    Duplicate,
    Missing,
    Unexpected,
    Unknown,
    Custom(Box<dyn std::error::Error + Send>),
    Multipart(multer::Error),
    Utf8(Utf8Error),
    Int(ParseIntError),
    Bool(ParseBoolError),
    Float(ParseFloatError),
    Addr(AddrParseError),
    Io(io::Error),
}

pub enum ExpectCt {
    Enforce(Duration),
    Report(Duration, Absolute),
    ReportAndEnforce(Duration, Absolute),
}

pub enum Hsts {
    Enable(Duration),
    IncludeSubDomains(Duration),
    Preload(Duration),
}

pub enum TempFile<'v> {
    File {
        file_name: Option<&'v FileName>,
        content_type: Option<ContentType>,
        path: Either<TempPath, PathBuf>,
        len: u64,
    },
    Buffered {
        content: &'v str,
    },
}
